import React, { useState, useEffect } from "react";
import { Container, Typography, Button, Box } from "@mui/material";
import axios from "axios";

const UserDetailsPage = () => {
  const [first, setfirst] = useState(second);

  const [user, setUser] = useState({
    name: "John Doe",
    time: new Date().toLocaleTimeString(),
    room: "101A",
  });

  // Function to fetch user data from an API
  const fetchUserData = async () => {
    try {
      const response = await axios.get("http://localhost:3005/check");
      setUser(response.data); // Assuming response.data is the updated user data
    } catch (error) {
      console.error("Error fetching user data:", error);
    }
  };

  // useEffect hook to fetch user data every 10 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      fetchUserData();
    }, 1000); // 10 seconds in milliseconds

    // Clear interval on component unmount
    return () => clearInterval(interval);
  }, []); // Empty dependency array to run effect only once on component mount

  return (
    <Container maxWidth="lg">
      <Box
        sx={{
          marginTop: 8,
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
        }}
      >
        <Typography variant="h4" component="h1" gutterBottom>
          User Details
        </Typography>
        <Typography variant="h2" gutterBottom>
          Name: {user.name}
        </Typography>
        <Typography variant="h2" gutterBottom>
          Time: {user.time}
        </Typography>
        <Typography variant="h2" gutterBottom>
          Room: {user.room}
        </Typography>
        <Button variant="contained" color="primary">
          Clock In
        </Button>
      </Box>
    </Container>
  );
};

export default UserDetailsPage;
